mapper.select:
select * from eqm e join eqm_img i
on e.eqm_cd = i.eqm_cd
where e.eqm_cd = #{eqmCd}

mapper.update:
update eqm
set
use_yn = #{frmUseYN} 
where eqm_cd = #{frmEqmCd}

mapper.delete:
DECLARE
BEGIN
delete from eqm
where eqm_cd = #{eqmCd};

delete from eqm_img
where eqm_cd = #{eqmCd};

END;
