mapper.update:
UPDATE PLAN
SET NOW_ST='진행'
WHERE PLAN_CD=#{planCd}

mapper.select:
	SELECT D.LINE_CD, D.EDCTS_CD, D.INDICA_CNT, D.ORDER_CNT, D.WK_FR_DT, E.PRDT_NM, I.INDICA_DT
	FROM INDICA_DTL D
	JOIN EDCTS E
	ON D.EDCTS_CD = E.EDCTS_CD
	JOIN INDICA I 
	ON I.INDICA_DT = D.INDICA_DT

	WHERE I.INDICA_DT >= #{indicaDt}
	  AND I.INDICA_DT <= #{indicaDt2}



     AND (I.NOW_ST = '미지시'
        or I.NOW_ST = '지시')


     AND I.NOW_ST = #{nowSt}


	AND E.PRDT_NM = #{prdtNm}
