mapper.select:
select rod.RSC_CD, rod.RSC_NM, rod.ORDR_CD, rod.ORDR_CNT, rod.RMN_CNT
from RSC_ORDR ro,
     RSC_ORDR_DTL rod
where ro.ORDR_CD = rod.ORDR_CD
  and ro.VEND_CD = #{vendCd}
  and rod.RMN_CNT != 0

  and PAPRD_CMND_DT >= #{startDt}
  and PAPRD_CMND_DT <= #{endDt}

order by PAPRD_CMND_DT

mapper.update:
update RSC_ORDR_DTL
set RMN_CNT = RMN_CNT - #{preIstCnt}
where ORDR_CD = #{ordrCd}
  and RSC_CD = #{rscCd}

mapper.select:
select rid.RSC_CD, rid.RSC_INSP_CD, rid.ORDR_CD, to_char(rid.INSP_DT, 'yyyy-mm-dd') inspDt,
ro.VEND_CD, rsc.RSC_NM, rid.INSP_CNT, rid.INSP_PASS_CNT, rid.INSP_FAIL_CNT, rsc.MNG_UNIT,
rsc.RSC_TYP, rsc.RSC_SPEC
from RSC_INSP_DTL rid, RSC_ORDR ro, RSC
where ro.ORDR_CD = rid.ORDR_CD
and rsc.RSC_CD = rid.RSC_CD
and rid.BOOL != 1

  and rid.INSP_DT >= #{startDt}
  and rid.INSP_DT <= #{endDt}

and ro.VEND_CD = #{vendCd}
and rid.RSC_CD = #{rscCd}
